package chap02_variables;

public class _06_floatDouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1. float타입 변수 선언, 초기화
		//F, f 붙이는 것에 차이가 없다.
		float fNum1 = 3.14F;
		float fNum2 = 3.14f;
		float fNum3 = 5.245F;
		
		//2. double타입 변수 선언, 초기화
		double dNum1 = 89.1245;
		double dNum2 = 11.984;
		
		//3. float타입의 연산
		//float타입의 변수 끼리 연산을 해도
		//double형으로 변환된 후 연산하고
		//연산의 결과도 double타입으로 나온다.
		double result1 = fNum1 + fNum2;
		
		//4. double타입의 연산
		double result2 = dNum1 / dNum2;
		
		
		
		
		
		
		
		
		
		
	}

}
